import React, { useState } from 'react';
import Navbar from './Navbar';
import Amazon from './Amazon';
import Cart from './Cart';
import data from '../data';
import '../styles/app.css';

const App = () => {
  const [cart, setCart] = useState([]);
  const [show, setShow] = useState(true);

  const addToCart = (item) => {
    const index = cart.findIndex((cartItem) => cartItem.id === item.id);

    if (index === -1) {
      const newCart = [...cart, { ...item, amount: 1 }];
      setCart(newCart);
    } else {
      const newCart = [...cart];
      newCart[index].amount++;
      setCart(newCart);
    }
  };

  const removeFromCart = (id) => {
    const index = cart.findIndex((cartItem) => cartItem.id === id);

    if (index === -1) return;

    const newCart = [...cart];
    newCart[index].amount--;

    if (newCart[index].amount === 0) {
      newCart.splice(index, 1);
    }

    setCart(newCart);
  };

  const updateItems = (id) => {
    const newItems = data.filter((item) => item.id !== id);
    setData(newItems);
  };

  return (
    <div>
      <Navbar size={cart.length} setShow={setShow} show={show} />
      {show ? (
        <Amazon handleClick={addToCart} />
      ) : (
        <Cart cart={cart} setCart={setCart} updateItems={updateItems} />
      )}
    </div>
  );
};

export default App;
